import React, { useState } from 'react';

function MainTranslator() {
  const [inputType, setInputType] = useState('text');
  const [resultVideoUrl, setResultVideoUrl] = useState(null);

  // Aquí irán los handlers para cada tipo de input y la lógica de conexión con el backend

  return (
    <div className="main-translator">
      <div className="input-selector">
        <button onClick={() => setInputType('text')}>Texto</button>
        <button onClick={() => setInputType('file')}>Archivo</button>
        <button onClick={() => setInputType('link')}>Enlace</button>
      </div>
      <div className="input-area">
        {inputType === 'text' && (
          <div>
            <label>Introduce el texto:</label>
            <input type="text" placeholder="Escribe aquí..." />
          </div>
        )}
        {inputType === 'file' && (
          <div>
            <label>Sube un archivo (audio/video):</label>
            <input type="file" accept="audio/*,video/*" />
          </div>
        )}
        {inputType === 'link' && (
          <div>
            <label>Pega el enlace (YouTube, etc.):</label>
            <input type="text" placeholder="https://..." />
          </div>
        )}
      </div>
      <div className="result-area">
        {resultVideoUrl && (
          <video src={resultVideoUrl} controls width="400" />
        )}
      </div>
    </div>
  );
}

export default MainTranslator;
