import React, { useState } from 'react';

function MainTranslator() {
  const [inputType, setInputType] = useState('text');
  const [inputValue, setInputValue] = useState('');
  const [resultVideoUrl, setResultVideoUrl] = useState(null);

  // Handlers b√°sicos (puedes conectar con el backend despu√©s)
  const handleInputChange = (e) => setInputValue(e.target.value);

  return (
    <div className="translator-container">
      <div className="translator-panel input-panel">
        <div className="panel-header">
          <span role="img" aria-label="input">üìù</span> Entrada
        </div>
        <div className="input-selector">
          <button className={inputType === 'text' ? 'active' : ''} onClick={() => setInputType('text')}>Texto</button>
          <button className={inputType === 'file' ? 'active' : ''} onClick={() => setInputType('file')}>Archivo</button>
          <button className={inputType === 'link' ? 'active' : ''} onClick={() => setInputType('link')}>Enlace</button>
        </div>
        <div className="input-area">
          {inputType === 'text' && (
            <textarea
              rows={6}
              placeholder="Escribe el texto aqu√≠..."
              value={inputValue}
              onChange={handleInputChange}
            />
          )}
          {inputType === 'file' && (
            <input type="file" accept="audio/*,video/*" />
          )}
          {inputType === 'link' && (
            <input
              type="text"
              placeholder="Pega el enlace aqu√≠..."
              value={inputValue}
              onChange={handleInputChange}
            />
          )}
        </div>
        <button className="translate-btn">Generar v√≠deo</button>
      </div>
      <div className="panel-separator">
        <span role="img" aria-label="arrow">‚û°Ô∏è</span>
      </div>
      <div className="translator-panel output-panel">
        <div className="panel-header">
          <span role="img" aria-label="output">üé¨</span> Resultado
        </div>
        <div className="result-area">
          {resultVideoUrl ? (
            <video src={resultVideoUrl} controls width="100%" />
          ) : (
            <div className="placeholder">
              <span role="img" aria-label="waiting">‚åõ</span>
              <p>El v√≠deo generado aparecer√° aqu√≠</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default MainTranslator;
